mapper.select:
select

  distinct


from api_resource




  order by ${orderByClause}


  limit #{startRow} , #{pageSize}

mapper.insert:
insert into api_resource (id, `name`, `path`, `method`, module_id, param_url, param_body, param_path)
values (#{id}, #{name}, #{path}, #{method}, #{moduleId}, #{paramUrl}, #{paramBody}, #{paramPath}) on duplicate key
update name = #{name}, `method` = #{method},param_url = #{paramUrl},param_body = #{paramBody},param_path = #{paramPath}
